'use strict';

var is = require('bpmn-js/lib/util/ModelUtil').is

var entryFactory = require('../../../factory/EntryFactory');

module.exports = function(group, element, translate) {

  if (is(element, 'bpmn:Task')) {

      var executableEntry = entryFactory.textBox2({
        id: 'process-is-Software',
        label: translate('نرم افزار'),
        modelProperty: 'Software'
      });

      group.entries = group.entries.concat(executableEntry);
  }
};