'use strict';

import * as entryFactory from '../factory/ElementFactory';

export function getElement(group, element) {

    var selectedVal = '';

    for(var k in element.businessObject.$attrs) {
        if (k == 'Importance') {
            selectedVal = element.businessObject.$attrs[k];
            break;
        }
    }

    var executableEntry = entryFactory.ratingBox({
        id: 'Importance',
        label: 'اهمیت',
        modelProperty: 'Importance',
        value: selectedVal
    });

    group.entries = group.entries.concat(executableEntry);
};