'use strict';

import * as entryFactory from '../factory/ElementFactory';

export function getElement(group, element) {

    var executableEntry = entryFactory.textBox({
        id: 'name',
        label: 'عنوان',
        modelProperty: 'name',
        value: (typeof element.businessObject.name !== "undefined") ? element.businessObject.name : ""
    });

    group.entries = group.entries.concat(executableEntry);

};
