'use strict';

import * as entryFactory from '../factory/ElementFactory';

export function getElement(group, element) {
    
    var selectedVal = '', roleVal = '';

    for(var k in element.businessObject.$attrs) {
        if (k == 'Responsible')
            selectedVal = element.businessObject.$attrs[k];
        else if(k == 'role_Responsible')
            roleVal = element.businessObject.$attrs[k];
    }

    var executableEntry = entryFactory.textBox2({
        id: 'Responsible',
        label: 'نیروی انسانی',
        modelProperty: 'Responsible',
        value: selectedVal,
        role_value: roleVal,
        placeHolder: 'نقش'
    });

    group.entries = group.entries.concat(executableEntry);
};